import React from 'react';
import './result.css';

import pera from '../../images/pera.jpeg';
import pedro from '../../images/pedro.jpeg';
import bela from '../../images/bela.jpeg';
import yes from '../../images/sim.jpeg';
import lino from '../../images/lino.jpeg';


let title;
let desc;
let image;

function setupPera() {
  title = "VÔCE ESTA NIVEL PÊRA DE MACHISMO";
  desc = "ESSE È O NIVEL MAIS PERIGOSO VOCÊ PRECISA SE DESCONTRUIR IMEDIATAMENTE!!!!!! >:("
  image = pera;
}

function setupSoftMachista() {
  title = "VÔCE É RESPEITADOR DE CASADAS";
  desc = "VC È UM GRANDE RESPEITADOR DE CASADAS MAS AINDA È HOMEN ENT È MACHISTA >:|"
  image = "https://i.pinimg.com/564x/0e/70/59/0e7059761087ee4e15700fbf216d877d.jpg";

}

function setupNotMachoMachista() {
  title = "VÔCE É RESPEITADOR FEMEAS";
  desc = "PARABÈN SEU NÌVEL DE MACHISMO È MUITO BAIXO MAS AINDA È HOMEN< ENT È MACHISTA >:|"
  image = lino;

}

function setupBadMachista() {
  title = "VÔCE É UM PATRIARCAL OPRESSOR DE RIGILIAO";
  desc = "COMO VOCÊ È ACAPAZ DE SER TÂO MACHISTA? E OPRESSOR, DESCONTRUA_SE!!!!!!!!!!!! >()"
  image = yes;

}

function setupComedorDeCasadas() {
  title = "VÔCE ESTA NIVEL COMEDOR DE CASADAS";
  desc = "ESSE NIVEL É MUITO FEIO VOCÊ PRECISA SE DESCONTRUIR PELO BEM DAS CASADAS !!!!!! >:(-)"
  image = "https://img.ifunny.co/images/54842bc4a53b31d246ad68b192dc9ddd0ba4b7a74cce441b94923794b3bbf374_1.webp"
}

function setupNotMachista() {
  title = "VÔCE É FEMEA E ESTÀ INSENTA DE MACHISMO";
  desc = "VC ESTA NO CAMINHO DESERRADo LUTE PELOS SEUS DIREITOS E SEJE FELIZ :D"
  image = bela;
}

function setupOpressora() {
  title = "VÔCE É FEMEA OPRESSORA REGILIOSA >>>>:((()))";
  desc = "COMO PODE SER TÂO OPRESSORA? !!!!!! >:-("
  image = yes;
}

function setupGilrMachista() {
  title = "VÔCE É FEMEA MACHISTA :-()";
  desc = "VC È TÂO MACHISTA QUE È QUASE HOMEN DESCONTRUA-SE !!!!!! >:-("
  image = pera;
}


function setupParaguaio() {
  title = "VÔCE É PARAGUAIIO :|";
  desc = "SLÁ MANO >:|"
  image = "https://i.ytimg.com/vi/k7fYDhXO3Mo/maxresdefault.jpg";
}

function setupIndioEmoBombado() {
  title = "VÔCE É INDIO EMO BOMBADO :|";
  desc = "MANO QUE Q TU TA FASENDO AKI ????????? :|"
  image = "data:image/jpeg;base64,";
}

function setupEMODefensor() {
  title = "VÔCE É INDO EMO BOMBADO RESPEITADOR DE CASADAS :-()";
  desc = "VC DEFENDE CASADAS DE PORTUGUESES EUROPEUS BRANCOS QUE VEM ROUBA-LAS VC È UM EXEMPLO !!!!!! :-D"
  image = "data:image/jpeg;base64,";
}

export default ({children, result, choices}) => {


  if (choices[0] === 100) {
    setupPera();

    if (result < 0) {
      setupNotMachoMachista();
    } else {
      if (result > -20 && result < 201) {
        setupSoftMachista()
      } else {

        if (result > -20 && result < 300) setupComedorDeCasadas();
      }
    }



    if (choices[1] === 0) setupSoftMachista();

    if (choices[1] == 10) setupBadMachista();

    if (choices[2] == 100) setupPera();


  }

  if (choices[0] === -1000) {
    setupNotMachista();
    if (result == -800) setupGilrMachista();

    if (choices[1] == 10) setupOpressora();

    if (choices[2] == 100) setupGilrMachista();

  }

  if (choices[0] === 10) {
    setupIndioEmoBombado();
    if (choices[2] === -110) setupEMODefensor();
  }

  if (choices[0] === 0) {
    setupParaguaio();
  }

  return (
    <div className="Result">

      <p className="title"> {title} </p>
      <p className="description"> {desc} </p>
      <img src={image} />

      {children}
    </div>
  )
}
